mapper.select:
SELECT COUNT(1)
FROM EMPLOYEE
WHERE EMAIL = #{email} AND ID <> #{id}

mapper.update:

UPDATE EMPLOYEE
SET PASSWORD = #{password},
UPDATED_AT = #{updatedAt,
jdbcType=TIMESTAMP}
WHERE ID = #{id}

mapper.select:
SELECT COUNT(*)
FROM employee e


		(LOWER(e.name) LIKE #{kw} OR LOWER(e.email) LIKE #{kw})


		AND e.department = #{dept}


		AND e.status = #{status}

SELECT * FROM (
SELECT t.*, ROWNUM rn FROM (
SELECT e.id, e.name, e.email, e.position, e.department, e.employment_type,  e.status,
e.hire_date, e.resign_date, e.updated_at
FROM employee e


		(LOWER(e.name) LIKE #{kw} OR LOWER(e.email) LIKE #{kw})


		AND e.department = #{dept}


		AND e.status = #{status}


ORDER BY e.updated_at DESC
) t
WHERE ROWNUM <= #{offset} + #{limit}
)
WHERE rn > #{offset}
